/** Global definitions for development **/

// for style loader
declare module '*.css' {
  const styles: any;
  export = styles;
}

declare module '*.png' {
  const img: any;
  export = img;
}

declare module '*.jpg' {
  const img: any;
  export = img;
}

declare module '*.gif' {
  const img: any;
  export = img;
}
